import React, { Component } from "react";
import Header from "../../components/header/Header";
import Footer from "../../components/footer/Footer";
// import GithubRepoCard from "../../components/githubRepoCard/GithubRepoCard";
import PublicationCard from "../../components/publicationsCard/PublicationCard";
// import Button from "../../components/button/Button";
import TopButton from "../../components/topButton/TopButton";
import { Fade } from "react-reveal";
import {
  // greeting,
  PublicationsHeader,
  publicationsHeader,
  Journal,
  Conference,
  Thesis,
  Workshops
} from "../../portfolio.js";
// import PublicationsData from "../../shared/opensource/Publications.json";
import "./Publications.css";
import PublicationsImg from "./PublicationsImg";

class Publications extends Component {
  render() {
    const theme = this.props.theme;
    return (
      <div className="Publication-main">
        <Header theme={theme} />
        <div className="basic-Publications">
          <Fade bottom duration={2000} distance="40px">
            <div className="Publication-heading-div">
              <div className="Publication-heading-img-div">
                {/* <img
											src={require(`../../assests/images/${PublicationsHeader["avatar_image_path"]}`)}
											alt=""
										/> */}
                <PublicationsImg theme={theme} />
              </div>
              <div className="Publication-heading-text-div">
                <h1
                  className="Publication-heading-text"
                  style={{ color: theme.text }}
                >
                  {PublicationsHeader.title}
                </h1>
                <p
                  className="Publication-header-detail-text subTitle"
                  style={{ color: theme.secondaryText }}
                >
                  {PublicationsHeader["description"]}
                </p>
              </div>
            </div>
          </Fade>
        </div>
        {/* <div className="repo-cards-div-main">
          {PublicationsData.data.map((repo) => {
            return <GithubRepoCard repo={repo} theme={theme} />;
          })}
        </div> */}
        {/* <Button
          text={"More Publications"}
          className="Publication-button"
          href={greeting.githubProfile}
          newTab={true}
          theme={theme}
        /> */}

        {/* Publications  */}
        {Journal.data.length > 0 ? (
          <div className="basic-Publication">
            <Fade bottom duration={2000} distance="40px">
              <div className="publications-heading-div">
                <div className="publications-heading-text-div">
                  <h1
                    className="publications-heading-text"
                    style={{ color: theme.text }}
                  >
                    {publicationsHeader.titleJ}
                  </h1>
                  <p
                    className="Publication-header-detail-text subTitle"
                    style={{ color: theme.secondaryText }}
                  >
                    {publicationsHeader["description"]}
                  </p>
                </div>
              </div>
            </Fade>
          </div>
        ) : null}

        <div className="repo-cards-div-main">
          {Journal.data.map((pub) => {
            return <PublicationCard pub={pub} theme={theme} />;
          })}
        </div>

        <Footer theme={this.props.theme} onToggle={this.props.onToggle} />
        <TopButton theme={this.props.theme} />
      </div>
    );
  }
}

export default Publications;
